import React, { Component } from "react";
import { Link } from "react-router-dom";

import ContentCard from "../../components/contentCard";
import LayoutContainer from "../../components/layoutContainer";
import MainContent from "../general/mainContent";
import PageTitle from "../../components/pageTitle";

export default class CreateHouse extends Component {
    constructor(props) {
        super(props);
        this.state = {
            houseName: '',
            houseAddress: '',
            housePrice: 0,
            houseImages: []
        };
    }

    handleChange = (e) => {
        this.setState({
            ...this.state,
            [e.target.name]: e.target.value.trim()
        });
    }

    handleCreateRoom() {
        const formData = new FormData();
        formData.append("name", this.state.houseName);
        formData.append("address", this.state.houseAddress);
        formData.append("price_per_night", this.state.housePrice);
        for (let i = 0; i < this.state.houseImages.length; i++) {
            formData.append("image" + i.toString(), this.state.houseImages[i])
        }
        const requestOptions = {
            method: 'POST',
            body: formData
        };

        fetch("/api/create-house/", requestOptions)
        .then((response) => response.json())
        .then((data) => {
        this.props.history.push('/house/' + data.name);
        console.log(data);
        });
    }

    handleHousePriceChange(e) {
        this.setState({
            housePrice: e.target.value
        });
    }

    handleHouseNameChange(e) {
        this.setState({
            houseName: e.target.value
        });
    }

    handleHouseAddressChange(e) {
        this.setState({
            houseAddress: e.target.value
        });
    }

    handleHouseImageChange = e => {
        if (e.target.files.length != 0) {
            const houseImages = this.state.houseImages.slice()
            houseImages.push(e.target.files[0])
            console.log(houseImages)
            this.setState({
                houseImages: houseImages
            });
        }
    }

    render() {
        return (
            <MainContent>
                <ContentCard>
                    <PageTitle title='Create a House' />
                        <form className='form' noValidate>
                        <label>
                            House Name
                            <input
                                required
                                id="houseName"
                                label="House Name"
                                name="houseName"
                                autoFocus
                                onChange={(e) => this.handleChange(e)}
                            />
                    </label>
                    <label>
                        House Address
                        <input
                            autoComplete="address"
                            required
                            id="houseAddress"
                            label="House Address"
                            name="houseAddress"
                            onChange={(e) => this.handleChange(e)}
                        />
                    </label>
                    <label>
                        Price Per Night
                        <input
                            required
                            type='number'
                            id="price"
                            label="Price Per Night"
                            name="price"
                            onChange={(e) => this.handleChange(e)}
                        />
                    </label>
                    <label>
                        House Images
                        <input
                            accept="image/*"
                            hidden
                            onChange={this.handleHouseImageChange}
                            id="imageInput"
                            multiple
                            type="file"
                            />
                    </label>

                            <input
                            accept="image/*"
                            hidden
                            onChange={this.handleHouseImageChange}
                            id="raised-button-file"
                            multiple
                            type="file"
                            />

                        <p>{this.state.houseImages.map(image => image['name'] + ", ")}</p>
                        <Button
                        color="primary"
                        variant="contained"
                        onClick={() => this.handleCreateRoom()}>
                        Add House
                        </Button>
                        <Button
                        color="secondary"
                        variant="contained"
                        to="/"
                        component={Link}>
                        Go Back
                        </Button>
                    </form>
                </ContentCard>
            </MainContent>
        );
    }
}
